#ifndef HORIZON_VISION_SINGLETON_SINGLETON_H_
#define HORIZON_VISION_SINGLETON_SINGLETON_H_
#include <assert.h>

namespace horizon {
namespace vision {
template <typename T>
class CSingleton {
  struct object_creator {
   public:
    object_creator() { CSingleton<T>::Instance(); }
    inline void do_nothing() const {}
  };

  static object_creator create_object;

 public:
  typedef T object_type;
  static object_type &Instance() {
    static object_type obj;
    // 据说这个do_nothing是确保create_object构造函数被调用
    // 这跟模板的编译有关
    create_object.do_nothing();
    return obj;
  }
};

template <typename T>
typename CSingleton<T>::object_creator CSingleton<T>::create_object;

}  // namespace vision
}  // namespace horizon
#endif  // HORIZON_VISION_SINGLETON_SINGLETON_H_
